import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FuncionariosApi } from 'src/app/api/funcionarios.api';
import { RotasConstant } from 'src/app/constants/rotas.constant';
import { FuncionarioDTO } from 'src/app/models/funcionario.dto';




@Component({
    templateUrl: './listagem.component.html',
    styleUrls: ['./listagem.component.scss']
})
export class ListagemComponent implements OnInit {

    listaFuncinarios: FuncionarioDTO[] = [];
    constructor(private funcionariosApi: FuncionariosApi, private router: Router,) { }

    ngOnInit() {
        debugger
        this.funcionariosApi.adquirirTodos().subscribe(retorno => {
            this.listaFuncinarios = retorno
        });
    }

    navegarCadastro() {
        this.router.navigate([RotasConstant.CADASTRO]);
    }
}
<table>
    <tr class="linha-principal">
        <td class="id">Id</td>
        <td>Nome</td>
        <td>Nº Departamento</td>
        <td>Salário</td>
        <td>Data de nascimento</td>
    </tr>
    <tr *ngFor="let funcinario of listaFuncinarios" class="linha">
        <td class="id">{{ funcinario.id }}</td>
        <td>{{ funcinario.nome }}</td>
        <td>{{ funcinario.numDep}}</td>
        <td>{{ funcinario.salario | currency:'BRL':true:'1.2-2'}}</td>
        <td>{{funcinario.nascimento | date: 'dd/MM/yyyy'}}</td>
    </tr>
</table>
<button (click)="navegarCadastro()">
    + Novo funcionário
</button>
