import { Component, OnInit } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';
import { Router } from '@angular/router';
import { FuncionariosApi } from 'src/app/api/funcionarios.api';
import { RotasConstant } from 'src/app/constants/rotas.constant';
import { FuncionarioDTO } from 'src/app/models/funcionario.dto';

export interface PeriodicElement {
  name: string;
  position: number;
  weight: number;
  symbol: string;
}

const ELEMENT_DATA: PeriodicElement[] = [
  {position: 1, name: 'Hydrogen', weight: 1.0079, symbol: 'H'},
  {position: 2, name: 'Helium', weight: 4.0026, symbol: 'He'},
  {position: 3, name: 'Lithium', weight: 6.941, symbol: 'Li'},
  {position: 4, name: 'Beryllium', weight: 9.0122, symbol: 'Be'},
  {position: 5, name: 'Boron', weight: 10.811, symbol: 'B'},
  {position: 6, name: 'Carbon', weight: 12.0107, symbol: 'C'},
  {position: 7, name: 'Nitrogen', weight: 14.0067, symbol: 'N'},
  {position: 8, name: 'Oxygen', weight: 15.9994, symbol: 'O'},
  {position: 9, name: 'Fluorine', weight: 18.9984, symbol: 'F'},
  {position: 10, name: 'Neon', weight: 20.1797, symbol: 'Ne'},
];

@Component({
    templateUrl: './listagem.component.html',
    styleUrls: ['./listagem.component.scss']
})
export class ListagemComponent implements OnInit {

  displayedColumns: string[] = ['position', 'name', 'weight', 'symbol'];
      dataSource : FuncionarioDTO[] = [];
   constructor(private funcionariosApi: FuncionariosApi,        private router: Router,) {}

    ngOnInit() {
    this.funcionariosApi.adquirirTodos().subscribe(retorno =>{
       this.dataSource = retorno
    });
    }

teste(){
    this.router.navigate([RotasConstant.CADASTRO]);
}
}
<button (click)="teste()">
    Cadastrar funcionario
</button>
<table>
    <tr class="linha">
        <td>Id</td>
        <td>Nome</td>
        <td>Nº Departamento</td>
        <td>Salário</td>
        <td>Data de nascimento</td>


    </tr>
    <tr *ngFor="let x of dataSource" class="linha">
        <td>{{ x.id }}</td>
        <td>{{ x.nome }}</td>
        <td>{{ x.numDep}}</td>
        <td>{{ x.salario}}</td>
        <td> {{x.nascimento | date: 'dd/MM/yyyy'}}</td>


    </tr>
</table>
