import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { FuncionarioDTO } from 'src/app/models/funcionario.dto';



@Component({
    templateUrl: './cadastro.component.html',
    styleUrls: ['./cadastro.component.scss']
})
export class CadastroComponent implements OnInit {
    // @ts-ignore
    formCliente: FormGroup;

    constructor(private formBuilder: FormBuilder) { }
    ngOnInit() {
        this.createForm();
    }



    createForm() {
        this.formCliente = this.formBuilder.group({
            nome: [],
            numDep: [],
            salario: [],
            nascimento: [],

        })
    }


    onSubmit() {
        // aqui você pode implementar a logica para fazer seu formulário salvar
        console.log(this.formCliente.value);

        // Usar o método reset para limpar os controles na tela
        this.formCliente.reset();
    }

}
<form [formGroup]="formCliente" (ngSubmit)="onSubmit()">

    <div class="form-group">
        <label for="nome">Nome</label>
        <input type="text" class="form-control" name="name" id="nome" formControlName="nome">
    </div>

    <div class="form-group">
        <label for="numDep">N° departamento</label>
        <input type="number" class="form-control" name="numDep" id="numDep" formControlName="numDep">
    </div>
    <div class="form-group">
        <label for="salario">Salário</label>
        <input type="number" class="form-control" name="salario" id="salario" formControlName="salario">
    </div>

    <div class="form-group">
        <label for="nascimento">Data de nascimento</label>
        <input type="date" class="form-control" name="nascimento" id="nascimento" formControlName="nascimento">
    </div>


    <input type="submit" class="btn btn-primary mt-4" value="Salvar">
    <a href="#" class="btn btn-secondary mt-4 ml-2">Cancelar</a>

</form>
