import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { FuncionariosApi } from 'src/app/api/funcionarios.api';
import { RotasConstant } from 'src/app/constants/rotas.constant';
import { FuncionarioDTO } from 'src/app/models/funcionario.dto';



@Component({
    templateUrl: './cadastro.component.html',
    styleUrls: ['./cadastro.component.scss']
})
export class CadastroComponent implements OnInit {
    // @ts-ignore
    formCliente: FormGroup;

    constructor(private formBuilder: FormBuilder, private funcionarioApi: FuncionariosApi, private router: Router) { }
    ngOnInit() {
        this.createForm();
    }
    createForm() {
        this.formCliente = this.formBuilder.group({
            nome: [],
            numDep: [],
            salario: [],
            nascimento: [],

        })
    }

    onSubmit() {
        let form = this.formCliente.value;
        this.funcionarioApi.salvar(new FuncionarioDTO(form.nome, form.numDep, form.salario, form.nascimento)).subscribe(retorno => {
            this.router.navigate([RotasConstant.LISTAGEM]);
        });

    }

}
<div class="conteiner-principal">
    <button class="">
        < </button>
            <div class="container-cadastro">
                <label class="titulo"> Novo funcionário</label>
                <form [formGroup]="formCliente" (ngSubmit)="onSubmit()">

                    <div class="form-group">
                        <label for="nome">Nome</label>
                        <input type="text" class="form-control" name="name" id="nome" formControlName="nome">
                    </div>

                    <div class="form-group">
                        <label for="numDep">N° departamento</label>
                        <input type="number" class="form-control" name="numDep" id="numDep" formControlName="numDep">
                    </div>
                    <div class="form-group">
                        <label for="salario">Salário</label>
                        <input type="number" class="form-control" name="salario" id="salario" formControlName="salario">
                    </div>

                    <div class="form-group">
                        <label for="nascimento">Data de nascimento</label>
                        <input type="date" class="form-control" name="nascimento" id="nascimento"
                            formControlName="nascimento">
                    </div>


                    <input type="submit" class="btn btn-primary mt-4" value="Cadastrar">


                </form>

            </div>
</div>
